addEventListener("direct-upload:initialize",e=>{const{target:d,detail:t}=e,{id:i,file:r}=t;d.insertAdjacentHTML("beforebegin",`\n    <div id="direct-upload-${i}" class="direct-upload direct-upload--pending">\n      <div id="direct-upload-progress-${i}" class="direct-upload__progress" style="width: 0%"></div>\n      <span class="direct-upload__filename">${r.name}</span>\n    </div>\n  `)}),addEventListener("direct-upload:start",e=>{const{id:d}=e.detail;document.getElementById(`direct-upload-${d}`).classList.remove("direct-upload--pending")}),addEventListener("direct-upload:progress",e=>{const{id:d,progress:t}=e.detail;document.getElementById(`direct-upload-progress-${d}`).style.width=`${t}%`}),addEventListener("direct-upload:error",e=>{e.preventDefault();const{id:d,error:t}=e.detail,i=document.getElementById(`direct-upload-${d}`);i.classList.add("direct-upload--error"),i.setAttribute("title",t)}),addEventListener("direct-upload:end",e=>{const{id:d}=e.detail;document.getElementById(`direct-upload-${d}`).classList.add("direct-upload--complete")});
